mapper.update:
update campus set

	status=#{status, jdbcType=SMALLINT}


	,close_reason=#{closeReason, jdbcType=VARCHAR}



		campus_id=#{campusId, jdbcType=VARCHAR}

update campus set campus_name=#{campusName,jdbcType=VARCHAR}, city_id=#{cityId,jdbcType=INTEGER}, address=#{address,jdbcType=VARCHAR}, notice=#{notice,jdbcType=VARCHAR}, deliver=#{deliver,jdbcType=VARCHAR}, open_time=#{openTime,jdbcType=TIMESTAMP}, close_time=#{closeTime,jdbcType=TIMESTAMP}, status=#{status,jdbcType=SMALLINT}, custom_service=#{customService,jdbcType=VARCHAR}, min_price=#{min_price,jdbcType=REAL}, delivery_fee=#{delivery_fee,jdbcType=REAL},
pic_url=#{pic_url,jdbcType=VARCHAR},
reach_time=#{reach_time,jdbcType=INTEGER}
where campus_id=#{campusId,jdbcType=VARCHAR}
