mapper.select:
SELECT food.food_id,food.campus_id, name, COMMENT, date, food_comment.grade, nickname, is_hidden FROM food_comment left join users on food_comment.phone = users.phone left join food on food_comment.food_id=food.food_id and food.campus_id=food_comment.campus_id
comment is not null and comment !="" and food.campus_id=#{campusId,jdbcType=INTEGER}
and food.food_id = #{search,jdbcType=INTEGER}
order by ${sort}
${order}
limit #{offset,jdbcType=INTEGER} , #{limit,jdbcType=INTEGER}

SELECT count(*) FROM food_comment
comment is not null and comment !="" and campus_id=#{campusId,jdbcType=INTEGER}
and food_id = #{search,jdbcType=VARCHAR}

mapper.delete:
delete
from
food_comment

	food_id=#{foodId,jdbcType=BIGINT}
	and
	date_format(date,'%Y-%m-%d')=#{date,jdbcType=DATE}
	and grade=#{grade}
	and campus_id=#{campusId,jdbcType=INTEGER}
