update food_special
set tag=0
where food_id = #{foodId,jdbcType=BIGINT} and campus_id=#{campusId,jdbcType=INTEGER}
and special_id = #{specialId,jdbcType=INTEGER}

update food_special


		name = #{name,jdbcType=VARCHAR},


		food_count = #{foodCount,jdbcType=INTEGER},


where food_id = #{foodId,jdbcType=BIGINT}
and special_id = #{specialId,jdbcType=INTEGER} and campus_id=#{campusId,jdbcType=INTEGER}

update food_special
