mapper.select:
select order_id,img_url,is_remarked,orders.food_id,orders.status,together_date,orders.campus_id as campus_id, orders.order_count as order_count , food.is_discount as is_discount, food.price as price,food.name as name, discount_price,food.message as message,pay_way,total_price,is_full_discount from orders,food where orders.food_id=food.food_id and orders.campus_id=food.campus_id and phone = #{phoneId,jdbcType=VARCHAR} And together_id=#{togetherId,jdbcType=VARCHAR}
and orders.status !=0
and orders.tag=1 order by together_date DESC,create_time DESC

SELECT together_id, orders.phone, food.name as name, order_count, address, orders.admin_phone, together_date, receiver.phone as receiver_phone, food.price as food_price, orders.price, is_discount, discount_price, users.nickname as admin_name FROM orders, food, receiver, users WHERE orders.food_id = food.food_id and orders.campus_id=food.campus_id AND orders.admin_phone = users.phone AND orders.rank = receiver.rank AND orders.phone = receiver.phone_id AND orders.status=#{status,jdbcType=SMALLINT} AND together_date>=#{time,jdbcType=DATE} and orders.tag=1
and orders.phone like "%${search}%"
order by together_date DESC,orders.phone ASC limit ${offset},${limit}

SELECT together_id, orders.phone, food. NAME AS NAME, order_count, address, orders.admin_phone, together_date, receiver.phone AS receiver_phone, food.price AS food_price, orders.price, is_discount, discount_price, users.nickname AS admin_name FROM orders LEFT JOIN food on orders.food_id = food.food_id and orders.campus_id=food.campus_id left join receiver on orders.rank = receiver.rank and orders.phone = receiver.phone_id left join users on orders.admin_phone=users.phone WHERE orders. STATUS =#{status,jdbcType=SMALLINT} AND orders.tag = 1 and orders.campus_id=#{campusId,jdbcType=INTEGER}
and orders.phone like "%${search}%"
order by together_date DESC,orders.phone ASC limit ${offset},${limit}

SELECT together_id, orders.admin_phone, orders.phone, food. NAME AS NAME, order_count, receiver.address, receiver.phone as receiver_phone, orders.admin_phone, food.price as food_price, orders.price, is_discount, discount_price, together_date FROM orders, food, receiver WHERE orders.food_id = food.food_id and orders.campus_id=food.campus_id AND orders.rank = receiver.rank AND orders.phone = receiver.phone_id AND orders.status=1 AND together_date>=#{time,jdbcType=DATE} and orders.tag=1
and orders.phone like "%${search}%"
order by together_date DESC,orders.phone ASC limit ${offset},${limit}

SELECT count(*) FROM orders WHERE together_date>=#{time,jdbcType=DATE} and orders.tag=1
and orders.phone like "%${search}%"
and status=#{status,jdbcType=SMALLINT}

select * from together_order where status !=1 and tag=1 and campus_id=#{campusId,jdbcType=INTEGER}
and DATE_FORMAT(together_date,"%Y-%m-%d")=#{date,jdbcType=DATE}
and DATE_FORMAT(together_date,"%Y-%m-%d")=CURDATE()
ORDER BY together_date ASC

mapper.update:
update orders set

	status=#{status, jdbcType=SMALLINT}


	,is_remarked=#{isRemarked, jdbcType=SMALLINT}


	together_id=#{togetherId, jdbcType=VARCHAR}
	and tag=1

		and order_id=#{orderId, jdbcType=BIGINT}


		and admin_phone is not null

update orders
set total_price=#{totalPrice}
where
order_id=#{orderId}

update
orders
set total_price=#{amount,jdbcType=REAL},
status=2 ,charge_id=#{chargeId,jdbcType=VARCHAR}
where
together_id=#{togetherId,jdbcType=VARCHAR}

mapper.select:
select
together_id,custome_phone as phone,together_date,total_price as
price,address,charge_id
from together_order


		tag=1 and
		status=#{status}


		and status=#{status}


		and campus_id=#{campusId,jdbcType=INTEGER}

SELECT sum(total_price)
from together_order

	campus_id=#{campusId,jdbcType=INTEGER} and status=4

		and together_date BETWEEN #{dateStart,jdbcType=TIMESTAMP}
		AND #{dateEnd,jdbcType=TIMESTAMP}


		and together_date>=#{dateToday,jdbcType=TIMESTAMP}


		AND pay_way=#{payWay,jdbcType=SMALLINT}
