mapper.insert:
insert into users (phone,
password, type,
nickname, img_url, last_login_date
)
values
(#{phone,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR},
#{type,jdbcType=SMALLINT},
#{nickname,jdbcType=VARCHAR},
#{imgUrl,jdbcType=VARCHAR}, #{lastLoginDate,jdbcType=DATE}
)

mapper.update:
update users
set password =
#{newPassword,jdbcType=VARCHAR}
where phone = #{phone,jdbcType=VARCHAR}

update users
set type=1 ,
campus_id=#{campusId,jdbcType=INTEGER}
where phone =
#{phone,jdbcType=VARCHAR}

update users
set type=2,
campus_id=#{campusId,jdbcType=INTEGER}
where phone =
#{phone,jdbcType=VARCHAR}

update users
set
type=0,
campus_id=#{campusId,jdbcType=INTEGER}
where phone =
#{phone,jdbcType=VARCHAR}

update users
set last_login_date =
#{date,jdbcType=DATE}
where phone = #{phone,jdbcType=VARCHAR}

mapper.select:
select users.phone, type, nickname, img_url, last_login_date,create_time,address as default_address,token from users left join receiver on users.phone = receiver.phone_id and receiver.tag=0
1=1
and (nickname like #{search,jdbcType=VARCHAR} or users.phone like #{search,jdbcType=VARCHAR})
order by ${sort}
${order}
limit #{offset,jdbcType=INTEGER} , #{limit,jdbcType=INTEGER}

mapper.update:
update users
